package com.BasicJava;

public class VariableExampe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		a=50; 
		char b='a';
		String c="Livetech";
		c="10";
		double d=12.342;
		float  h=25;
		
		System.out.println("The value of an integer a is:"+a);
		System.out.println("The value of  character b is:"+b);
		System.out.println("The value of  String c is:"+c);
		System.out.println("The value of double d is:"+d);
		System.out.println("The value of float h is:"+h);
	}
}
